import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Typography, Card, CardContent, Grid, Button, CardMedia } from '@mui/material';

const Saved = () => {
  const [newsData, setNewsData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(
          'https://newsapi.org/v2/top-headlines?country=us&apiKey=300599394c264b33b1f958a3ffd4777b'
        );
        setNewsData(response.data.articles);
      } catch (error) {
        console.log(error);
      }
    };

    fetchData();
  }, []);

  const handleCardHover = (event) => {
    event.currentTarget.style.transform = 'scale(1.05)';
    event.currentTarget.style.boxShadow = '0 4px 10px rgba(0, 0, 0, 0.2)';
  };

  const handleCardLeave = (event) => {
    event.currentTarget.style.transform = 'scale(1)';
    event.currentTarget.style.boxShadow = 'none';
  };

  return (
    <div>
      <Typography variant="h4" sx={{ marginBottom: '1rem', color: '#333' }}>
        Latest News
      </Typography>
      <Grid container spacing={2}>
        {newsData.map((newsItem) => (
          <Grid item xs={12} sm={6} md={4} key={newsItem.publishedAt}>
            <Card
              sx={{
                height: '100%',
                display: 'flex',
                flexDirection: 'column',
                borderRadius: '8px',
                overflow: 'hidden',
                transition: 'transform 0.2s, box-shadow 0.2s',
                '&:hover': {
                  transform: 'scale(1.05)',
                  boxShadow: '0 4px 10px rgba(0, 0, 0, 0.2)',
                },
              }}
              onMouseEnter={handleCardHover}
              onMouseLeave={handleCardLeave}
            >
              <CardMedia
                component="img"
                image={newsItem.urlToImage}
                alt={newsItem.title}
                sx={{ height: 0, paddingTop: '56.25%', objectFit: 'cover' }}
              />
              <CardContent sx={{ flex: '1 0 auto' }}>
                <Typography variant="h6" sx={{ marginBottom: '0.5rem' }}>
                  {newsItem.title}
                </Typography>
                <Typography variant="subtitle2" sx={{ color: '#555', marginBottom: '0.5rem' }}>
                  {newsItem.author}
                </Typography>
                <Typography variant="body2" sx={{ color: '#777', marginBottom: '1rem' }}>
                  {newsItem.description}
                </Typography>
                <Button
                  variant="contained"
                  href={newsItem.url}
                  target="_blank"
                  rel="noopener noreferrer"
                  sx={{ backgroundColor: '#ff5722', color: '#fff' }}
                >
                  Read More
                </Button>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </div>
  );
};

export default Saved;
