import React, { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom'
import {Box, Typography, Button, TextField, Link} from '@mui/material'
import rpaper from './rpaper.jpg'
import google from './google.png'
import { auth, provider } from './config'
import { signInWithPopup } from 'firebase/auth'
import Home from './Home'

const Signup = () => {
  const [email, setEmail] = React.useState("");
    const [value,setValue] = useState('')
    const navigate = useNavigate();
    const handleClick = () =>{
        signInWithPopup(auth, provider).then((data)=>{
            console.log(data.user)
            setValue(data.user.email)
            localStorage.setItem("email",data.user.email)
            navigate('/Home')
            if((error) => {
                console.log('Error signing in with Google:', error);
              });

        })
    }





    function createUserWithEmailAndPasswordF(){
      // if password is more than 8 characters long
      if (password.length>=8){
        console.log("password lenght correct")
      }else{
        console.log("password should be at least 8 characters long")
        return
      }

      createUserWithEmailAndPassword(auth, email, password)
    .then((userCredential) => {
      // Signed in 
      const user = userCredential.user;
      // ...
      console.log("user created"+ userCredential.user.uid)
    })
    .catch((error) => {
      const errorCode = error.code;
      const errorMessage = error.message;
      // ..
    });
    }
    const onChangeHandlerP = event => {
      setPassword(event.target.value);
   };
   const onChangeHandlerE = event => {
    setEmail(event.target.value);
 };









    useEffect(()=>{
        setValue(localStorage.getItem('email'))
        // 
    },[]);

  return (
    <Box
      sx={{
        backgroundImage: `url(${rpaper})`,
        backgroundSize: 'cover',
        backgroundRepeat: 'no-repeat',
        backgroundPosition: 'center',
        height: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
      }}
      component="div"
    >
      <Box
        sx={{
          backgroundColor: 'rgba(255, 255, 255, 0.7)',
          padding: '20px',
          borderRadius: '20px',
        }}
      >
        <Typography variant="h4" align="center" gutterBottom>
          SignUp
        </Typography>
        <form>
          <TextField
            id="email"
            label="Email"
            variant="outlined"
            fullWidth
            margin="normal"
            onChange={onChangeHandlerE}
          />
          <TextField
            id="password"
            label="Password"
            variant="outlined"
            type="password"
            fullWidth
            margin="normal"
            onChange={onChangeHandlerP}
          />
          <Button
          variant="contained" onClick={createUserWithEmailAndPasswordF} fullWidth type="submit">
            Signup
          </Button>
        </form>
        <Typography variant="body1" align="center" mt={2}>
          Already have an account? <Link href="Login">Login</Link>
        </Typography>
        <Typography variant="body1" align="center" mt={2}>
          Or
          {
          <Button
            onClick={ handleClick }
            variant="outlined"
            fullWidth
            sx={{ display: 'flex', alignItems: 'center', justifyContent: 'center', mt: 2 }}
          >
            <img src={google} alt="Google Logo" sx={{ width: 20, height: 20, mr: 1 }} style={{ marginRight: '8px' }} />
            Signin with Google
          </Button>
                }
        </Typography>
      </Box>

    </Box>
  )
}

export default Signup
