import React, { useEffect, useState } from 'react'
import {Box, Typography, Button, TextField, Link} from '@mui/material'
import rpaper from './rpaper.jpg'
import google from './google.png'
import { auth, provider } from './config'
import { signInWithPopup } from 'firebase/auth'

const Signup = () => {
    const [value,setValue] = useState('')
    const handleClick = () =>{
        signInWithPopup(auth, provider).then((data)=>{
            setValue(data.user.email)
            localStorage.setItem("email",data.user.email).catch((error) => {
                console.log('Error signing in with Google:', error);
              });

        })
    }
    useEffect(()=>{
        setValue(localStorage.getItem('email'))
    })




    const handleSubmit = (e) => {
        e.preventDefault();
        const email = e.target.email.value;
        const password = e.target.password.value;
    
        auth
          .createUserWithEmailAndPassword(email, password)
          .then((userCredential) => {
            const user = userCredential.user;
            console.log('User signed up:', user);
          })
          .catch((error) => {
            console.log('Error signing up:', error);
          });
      };






  return (
    <Box
      sx={{
        backgroundImage: `url(${rpaper})`,
        backgroundSize: 'cover',
        backgroundRepeat: 'no-repeat',
        backgroundPosition: 'center',
        height: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
      }}
      component="div"
    >
      <Box
        sx={{
          backgroundColor: 'rgba(255, 255, 255, 0.7)',
          padding: '20px',
          borderRadius: '20px',
        }}
      >
        <Typography variant="h4" align="center" gutterBottom>
          SignUp
        </Typography>
        <form>
          <TextField
            id="email"
            label="Email"
            variant="outlined"
            fullWidth
            margin="normal"
          />
          <TextField
            id="password"
            label="Password"
            variant="outlined"
            type="password"
            fullWidth
            margin="normal"
          />
          <Button
          onClick={ handleClick }
        variant="contained" fullWidth type="submit">
            Signup
          </Button>
        </form>
        <Typography variant="body1" align="center" mt={2}>
          Already have an account? <Link href="Login">Login</Link>
        </Typography>
        <Typography variant="body1" align="center" mt={2}>
          Or
          {
          <Button
            onClick={ handleClick }
            variant="outlined"
            fullWidth
            sx={{ display: 'flex', alignItems: 'center', justifyContent: 'center', mt: 2 }}
          >
            <img src={google} alt="Google Logo" sx={{ width: 20, height: 20, mr: 1 }} style={{ marginRight: '8px' }} />
            Signin with Google
          </Button>
                }
        </Typography>
      </Box>

    </Box>
  )
}

export default Signup
