import React, { useEffect, useState } from 'react'
import {Card, CardActions, CardMedia, CardContent, Button, Typography, Grid, Box, make } from '@mui/material';

const useStyles = makeStyles(() => ({
    card: {
      height: '100%', // Set a fixed height for each card
      display: 'flex', // Use flexbox to align card content vertically
      flexDirection: 'column', // Align card content vertically
    },
  }));

const Articles = () => {
    const classes = useStyles();
    const [notes, setNotes] = useState([])

    useEffect(()=>{
        const url="https://newsapi.org/v2/top-headlines?country=us&apiKey=300599394c264b33b1f958a3ffd4777b";
        const fetchdata = async() =>{
            try{
                const response=await fetch(url);
                const jsondata=await response.json();
                // console.log(jsondata.articles)
                setNotes(jsondata.articles)
            }
            catch(err)
            {
                console.log("error",err);
            }
        };
        fetchdata();

    },[]);

  return (
    <div>
        <Box p={15}>
        <Grid container spacing={-9}>
           {notes.map((dataobj, idx)=>{
            return(
                <Grid item xs={12} sm={6} md={4}>
                <Card sx={{ maxWidth: 345 }} key={idx}>
                <CardMedia
                    component="img"
                    alt="green iguana"
                    height="140"
                    image= {dataobj.urlToImage}
                />
            
                <CardContent>
                    <Typography gutterBottom variant="h5" component="div">
                    {dataobj.title}
                    </Typography>
                    <p>{dataobj.publishedAt}</p>
                    <Typography variant="body2" color="text.secondary">
                   {dataobj.description}
                    </Typography>
                </CardContent>
                <CardActions>
                    <Button size="small">Read More</Button>
                    <Button size="small">Read More</Button>
                </CardActions>
                </Card>
                </Grid>
            )
            
           })
            }
        </Grid>
        </Box>
    </div>
  )
}






export default Articles
